import axios from 'axios';

// CSRF 토큰을 가져와 Axios 인스턴스에 추가
const getCsrfToken = async () => {
    try {
        const response = await axios.get('https://a59fb33dbf23.ngrok.app/odsay/set-csrf-token/', { withCredentials: true });
        console.log('MapView.js >> CSRF token received:', response.data);
        return response.data.csrfToken;
    } catch (error) {
        console.error('MapView.js >> Error fetching CSRF token:', error);
        throw error;  // 에러 발생 시 throw로 전달
    }
};

// ODSAY API 키
const ODSAY_API_KEY = process.env.VUE_APP_ODSAY_API_KEY;

// Axios 인스턴스 생성 및 기본 설정 추가
const axiosInstance = axios.create({
    baseURL: 'https://a59fb33dbf23.ngrok.app',  // ngrok 도메인 설정
    withCredentials: true  // 자격 증명 포함
});

axiosInstance.interceptors.request.use(async (config) => {
    try {
        const token = await getCsrfToken();
        config.headers['X-CSRFToken'] = token;
        console.log('MapView.js >> CSRF token set in request headers:', token);
        return config;
    } catch (error) {
        console.error('MapView.js >> Failed to set CSRF token in request headers:', error);
        return Promise.reject(error);  // 에러 발생 시 요청 중단
    }
});


export default {
    props: {
        memHome: String,
        startPoint: String,
        endPoint: String,
        memYoungY: Boolean,
        memYoungN: Boolean,
        memSubsidiaryYn: Boolean
    },
    data() {
        return {
            localStartPoint: this.startPoint,
            localEndPoint: this.endPoint,
            routes: []
        };
    },
    methods: {
        async geocode(address) {
            try {
                const response = await axiosInstance.post('/odsay/geocode/', { address });
                return response.data;
            } catch (error) {
                console.error('MapView.js >> Error geocoding address:', error);
                throw error;
            }
        },
        async findRoute() {
            try {
                console.log('MapView.js >> Finding route with start point:', this.localStartPoint, 'and end point:', this.localEndPoint);

                // Geocoding 주소를 통해 좌표를 가져오는 부분 (백엔드에서 처리)
                const startResponse = await axiosInstance.post('/odsay/geocode/', { address: this.localStartPoint });
                const endResponse = await axiosInstance.post('/odsay/geocode/', { address: this.localEndPoint });

                const { x: sx, y: sy } = startResponse.data;
                const { x: ex, y: ey } = endResponse.data;

                console.log('MapView.js >> Start coordinates:', { sx, sy });
                console.log('MapView.js >> End coordinates:', { ex, ey });

                // ODSAY API를 통해 경로 찾기 요청
                const odsasApiUrl = `https://api.odsay.com/v1/api/searchPubTransPathT?SX=${sx}&SY=${sy}&EX=${ex}&EY=${ey}&apiKey=${encodeURIComponent(ODSAY_API_KEY)}`;

                const routeResponse = await axios.get(odsasApiUrl);
                console.log('MapView.js >> ODSAY API response:', routeResponse.data);

                if (routeResponse.data && routeResponse.data.result && routeResponse.data.result.path) {
                    this.routes = routeResponse.data.result.path;
                } else {
                    console.error('MapView.js >> Unexpected API response format:', routeResponse.data);
                    this.routes = []; // 데이터가 없을 경우 빈 배열로 초기화합니다.
                }
            } catch (error) {
                console.error('MapView.js >> Error finding route:', error);
                if (error.response) {
                    console.error('MapView.js >> Error response data:', error.response.data);
                }
            }
        },
        formatTime(minutes) {
            const hours = Math.floor(minutes / 60);
            const mins = minutes % 60;
            return `${hours}시간 ${mins}분`;
        },
        getLineClass(trafficType, subwaycode) {
            if (trafficType === 1) {
                return 'bus';
            } else if (trafficType === 2) {
                return `sub${subwaycode}`;
            } else {
                return 'walk';
            }
        },
        getAction(subPath) {
            if (subPath.trafficType === 1) {
                return '승차';
            } else if (subPath.trafficType === 2) {
                return '환승';
            } else {
                return '도보';
            }
        },
        initializeMap() {
            if (window.naver) {
                var mapOptions = {
                    center: new naver.maps.LatLng(37.5665, 126.9780),
                    zoom: 10
                };
                this.map = new naver.maps.Map('map', mapOptions);
            } else {
                console.error('MapView.js >> Naver Maps API is not loaded.');
            }
        }
    },
    async mounted() {
        this.initializeMap();
    },
};
