export function redirectToNaverLogin() {
    const apiBaseUrl = process.env.VUE_APP_API_BASE_URL;
    window.location.href = `${apiBaseUrl}/user/naver/login`;
}

export default {
    name: 'LoginPage',
    data() {
        return {
            message: '',
            loginUrls: {},
        }
    },
    created() {
        const apiBaseUrl = process.env.VUE_APP_API_BASE_URL;
        fetch(`${apiBaseUrl}/user/api/login/`)
            .then(response => response.json())
            .then(data => {
                this.message = data.message
            }),
        fetch(`${apiBaseUrl}/api/social-login-urls/`)
            .then(response => response.json())
            .then(data => {
                this.loginUrls = data;
            });
    },
    methods: {
        redirectToNaverLogin() {
            const apiBaseUrl = process.env.VUE_APP_API_BASE_URL;
            window.location.href = `${apiBaseUrl}/user/naver/login`;
        },
        redirectToSocialLogin(provider) {
            const url = this.loginUrls[provider];
            if (url) {
                window.location.href = url;
            }
    }
}
}
