import axios from 'axios';

// CSRF 토큰을 가져와 Axios 인스턴스에 추가
const getCsrfToken = async () => {
    try {
        const response = await axios.get('https://d4182847ec99.ngrok.app/odsay/set-csrf-token/');
        return response.data.csrfToken;
    } catch (error) {
        console.error('Error fetching CSRF token:', error);
    }
};

// Axios 인스턴스 생성 및 기본 설정 추가
const axiosInstance = axios.create({
    baseURL: 'https://d4182847ec99.ngrok.app',  // ngrok 도메인 설정
    withCredentials: true  // 자격 증명 포함
});

axiosInstance.interceptors.request.use(async (config) => {
    const token = await getCsrfToken();
    config.headers['X-CSRFToken'] = token;
    return config;
});


export default {
    props: {
        memHome: String,
        startPoint: String,
        endPoint: String,
        memYoungY: Boolean,
        memYoungN: Boolean,
        memSubsidiaryYn: Boolean
    },
    data() {
        return {
            localStartPoint: this.startPoint,
            localEndPoint: this.endPoint,
            routes: []
        };
    },
    methods: {
        async findRoute() {
            try {
                const response = await axiosInstance.post('https://d4182847ec99.ngrok.app/odsay/find-route/', {
                    startPoint: this.localStartPoint,
                    endPoint: this.localEndPoint
                });

                console.log('API response:', response.data); // API 응답을 콘솔에 출력합니다.

                if (response.data && response.data.result && response.data.result.path) {
                    this.routes = response.data.result.path;
                } else {
                    console.error('Unexpected API response format:', response.data);
                    this.routes = []; // 데이터가 없을 경우 빈 배열로 초기화합니다.
                }
            } catch (error) {
                console.error('Error finding route:', error);
                if (error.response) {
                    console.error('Error response data:', error.response.data);
                }
            }
        },
        formatTime(minutes) {
            const hours = Math.floor(minutes / 60);
            const mins = minutes % 60;
            return `${hours}시간 ${mins}분`;
        },
        getLineClass(trafficType, subwaycode) {
            if (trafficType === 1) {
                return 'bus';
            } else if (trafficType === 2) {
                return `sub${subwaycode}`;
            } else {
                return 'walk';
            }
        },
        getAction(subPath) {
            if (subPath.trafficType === 1) {
                return '승차';
            } else if (subPath.trafficType === 2) {
                return '환승';
            } else {
                return '도보';
            }
        },
        initializeMap() {
            if (window.naver) {
                var mapOptions = {
                    center: new naver.maps.LatLng(37.5665, 126.9780),
                    zoom: 10
                };
                this.map = new naver.maps.Map('map', mapOptions);
            } else {
                console.error('Naver Maps API is not loaded.');
            }
        }
    },
    async mounted() {
        this.initializeMap();
    },
};
