import axios from 'axios';

function getCookie(name) {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
        const cookies = document.cookie.split(';');
        for (let i = 0; i < cookies.length; i++) {
            const cookie = cookies[i].trim();
            if (cookie.substring(0, name.length + 1) === (name + '=')) {
                cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                break;
            }
        }
    }
    return cookieValue;
}

export default {
    props: {
        memHome: String,
        startPoint: String,
        endPoint: String,
        memYoungY: Boolean,
        memYoungN: Boolean,
        memSubsidiaryYn: Boolean
    },
    data() {
        return {
            localStartPoint: this.startPoint,
            localEndPoint: this.endPoint,
            routeInfo: {
                start: '',
                end: '',
                distance: '',
                duration: ''
            },
            map: null,
            csrfToken: getCookie('csrftoken')
        };
    },
    methods: {
        async findRoute() {
            try {
                const apiBaseUrl = process.env.VUE_APP_API_BASE_URL;
                const response = await axios.post(`${apiBaseUrl}/odsay/find-route/`, {
                    startPoint: this.localStartPoint,
                    endPoint: this.localEndPoint
                }, {
                    headers: {
                        'X-CSRFToken': this.csrfToken
                    }
                });
                this.routeInfo = response.data;
                this.displayRouteInfo(this.routeInfo);
            } catch (error) {
                console.error('Error finding route:', error);
            }
        },
        displayRouteInfo(routeInfo) {
            var results = document.getElementById('results');
            results.innerHTML = `
                <h2>경로 정보</h2>
                <p>출발지: ${routeInfo.start}</p>
                <p>도착지: ${routeInfo.end}</p>
                <p>거리: ${routeInfo.distance}</p>
                <p>시간: ${routeInfo.duration}</p>
            `;
        },
        async initializeCsrfToken() {
            const apiBaseUrl = process.env.VUE_APP_API_BASE_URL;
            const response = await axios.get(`${apiBaseUrl}/odsay/set-csrf-token/`, { withCredentials: true });
            this.csrfToken = getCookie('csrftoken');
        },
        initializeMap() {
            if (window.naver) {
                var mapOptions = {
                    center: new naver.maps.LatLng(37.5665, 126.9780),
                    zoom: 10
                };
                this.map = new naver.maps.Map('map', mapOptions);
            } else {
                console.error('Naver Maps API is not loaded.');
            }
        }
    },
    async mounted() {
        await this.initializeCsrfToken();
        this.initializeMap();
    }
};
