import { createApp } from 'vue'
import App from './App.vue'
import router from './router' ;
import axios from 'axios';

// Axios 기본 설정
axios.defaults.withCredentials = true;  // withCredentials 설정 추가
axios.defaults.xsrfCookieName = 'csrftoken';
axios.defaults.xsrfHeaderName = 'X-CSRFToken';

// CSRF 토큰을 가져와 Axios에 설정
const apiBaseUrl = process.env.VUE_APP_API_BASE_URL || 'http://localhost:8000';
axios.get(`${apiBaseUrl}/map/set-csrf-token/`)
    .then(response => {
        axios.defaults.headers.common['X-CSRFToken'] = response.data.csrfToken;
    })
    .catch(error => {
        console.error('Error getting CSRF Token: ', error) ;
    });

// Axios 를 Vue 인스턴스에 플러그인으로 추가
const app = createApp(App);
app.config.globalProperties.$http = axios;

app.use(router).mount('#app');
