import axios from 'axios';

export default {
    name: 'LoginPage',
    data() {
        return {
            message: '',
            loginUrls: {
                naver: '',
                google: '',
                kakao: '',
            },
        };
    },
    created() {
        const apiBaseUrl = process.env.VUE_APP_API_BASE_URL;

        // 소셜 로그인 URL 가져오기
        axios.get(`${apiBaseUrl}/api/accounts/social-login-urls/`)
            .then(response => {
                this.loginUrls = response.data;
            })
            .catch(error => {
                console.error('Failed to fetch social login URLs:', error);
            });
    },
    methods: {
        redirectToSocialLogin(provider) {
            const url = this.loginUrls[provider];
            if (url) {
                window.location.href = url;
            } else {
                console.error(`No URL found for provider: ${provider}`);
            }
        }
    }
};
